        // --- COSMIC BACKGROUND ---
        function createCosmicBackground() {
            const starField = document.getElementById('starField');

            for (let i = 0; i < 150; i++) {
                const star = document.createElement('div');
                star.className = 'star';
                star.style.left = Math.random() * 100 + '%';
                star.style.top = Math.random() * 100 + '%';
                const size = Math.random() * 3 + 1;
                star.style.width = size + 'px';
                star.style.height = size + 'px';
                star.style.setProperty('--duration', (Math.random() * 3 + 2) + 's');
                star.style.setProperty('--opacity', Math.random() * 0.7 + 0.3);
                star.style.animationDelay = Math.random() * 5 + 's';
                starField.appendChild(star);
            }

            setInterval(() => {
                const shooting = document.createElement('div');
                shooting.className = 'shooting-star';
                shooting.style.top = Math.random() * 50 + '%';
                shooting.style.left = Math.random() * 50 + '%';
                shooting.style.animationDuration = (Math.random() * 2 + 2) + 's';
                starField.appendChild(shooting);
                setTimeout(() => shooting.remove(), 4000);
            }, 3000);
        }

        // --- NAVIGATION & SECTIONS ---
        function showSection(sectionId) {
            document.querySelectorAll('section').forEach(section => {
                section.classList.remove('active');
                section.style.display = 'none';
            });

            const target = document.getElementById(sectionId);
            target.style.display = 'flex';
            setTimeout(() => target.classList.add('active'), 10);

            window.scrollTo(0, 0);

            if (sectionId === 'database' && !window.dbInitialized) {
                renderDatabase();
                window.dbInitialized = true;
            }
        }

        function toggleMobileMenu() {
            const navLinks = document.getElementById('navLinks');
            navLinks.style.display = navLinks.style.display === 'flex' ? 'none' : 'flex';
        }

        // --- MODALS ---
        function openModal(modalId) {
            document.getElementById(modalId).style.display = 'flex';
            document.body.style.overflow = 'hidden';
        }

        function closeModal(event, modalId) {
            if (event.target.classList.contains('modal-overlay')) {
                document.getElementById(modalId).style.display = 'none';
                document.body.style.overflow = 'auto';
            }
        }

        function closeModalById(modalId) {
            document.getElementById(modalId).style.display = 'none';
            document.body.style.overflow = 'auto';
        }

        // --- DATABASE ---
        function renderDatabase(filter = '') {
            const grid = document.getElementById('qaGrid');
            grid.innerHTML = '';

            let count = 0;
            database.forEach((item, index) => {
                if (filter && !item.question.toLowerCase().includes(filter.toLowerCase()) &&
                    !item.answer.toLowerCase().includes(filter.toLowerCase())) {
                    return;
                }

                count++;
                const card = document.createElement('div');
                card.className = 'qa-card';
                card.style.animationDelay = (index * 0.05) + 's';
                card.innerHTML = `
                    <div class="qa-number">‚Ññ${index + 1}</div>
                    <div class="qa-question">${item.question}</div>
                    <div class="qa-answer">${item.answer}</div>
                `;
                grid.appendChild(card);
            });

            if (count === 0) {
                grid.innerHTML = '<div class="no-results">–°–∞–≤–æ–ª–µ —ë—Ñ—Ç –Ω–∞—à—É–¥.</div>';
            }
        }

        function filterQuestions() {
            const input = document.getElementById('searchInput');
            renderDatabase(input.value);
        }

        // --- CHAT ---
        function findAnswer(question) {
            const q = question.toLowerCase().trim();
            if (!q) return null;

            for (let item of database) {
                if (item.question.toLowerCase().includes(q) || q.includes(item.question.toLowerCase())) {
                    return item.answer;
                }
            }

            const keywords = ['—Ä—É—Å—Ç–∞–º', '—Å—É“≥—Ä–æ–±', '—Å–∏—ë–≤—É—à', '–∑–æ–ª', '—Å–∏–º—É—Ä“ì', '—Ä–∞—Ö—à', '–∑–∞“≥“≥–æ–∫', '—Ñ–∞—Ä–∏–¥—É–Ω', '–∫–∞–π–∫–æ–≤—É—Å', '–¥–µ–≤', '“∑–∞–Ω–≥', '–∞—Ñ—Ä–æ—Å–∏—ë–±', '—à–∞“≥–Ω–æ–º–∞', '—Ñ–∏—Ä–¥–∞–≤—Å”£'];
            const isRelated = keywords.some(k => q.includes(k));

            if (!isRelated && q.length > 3) {
                return "–ò–Ω —Å–∞–≤–æ–ª –±–∞ –º–∞–≤–∑”Ø–∏ ¬´–®–æ“≥–Ω–æ–º–∞¬ª –¥–∞—Ö–ª nadora–¥";
            }

            return "–ò–Ω —Å–∞–≤–æ–ª –±–∞ –º–∞–≤–∑”Ø–∏ ¬´–®–æ“≥–Ω–æ–º–∞¬ª –¥–∞—Ö–ª nadora–¥";
        }

        function sendChatMessage() {
            const input = document.getElementById('chatInput');
            const messages = document.getElementById('chatMessages');
            const typing = document.getElementById('typingIndicator');
            const btn = document.getElementById('sendBtn');

            const question = input.value.trim();
            if (!question) return;

            const userDiv = document.createElement('div');
            userDiv.className = 'message user';
            const time = new Date().toLocaleTimeString('tg-TJ', { hour: '2-digit', minute: '2-digit' });
            userDiv.innerHTML = `
                <div class="message-avatar">üë§</div>
                <div>
                    <div class="message-content">${escapeHtml(question)}</div>
                    <div class="message-time">${time}</div>
                </div>
            `;
            messages.appendChild(userDiv);
            input.value = '';
            messages.scrollTop = messages.scrollHeight;

            typing.classList.add('active');
            btn.disabled = true;

            setTimeout(() => {
                typing.classList.remove('active');

                const answer = findAnswer(question);
                const isOut = answer === "–ò–Ω —Å–∞–≤–æ–ª –±–∞ –º–∞–≤–∑”Ø–∏ ¬´–®–æ“≥–Ω–æ–º–∞¬ª –¥–∞—Ö–ª nadora–¥";
                const botTime = new Date().toLocaleTimeString('tg-TJ', { hour: '2-digit', minute: '2-digit' });

                const botDiv = document.createElement('div');
                botDiv.className = 'message bot';
                botDiv.innerHTML = `
                    <div class="message-avatar">${isOut ? '‚ö†Ô∏è' : 'üìú'}</div>
                    <div>
                        <div class="message-content ${isOut ? 'out-of-topic' : ''}">${answer}</div>
                        <div class="message-time">${botTime}</div>
                    </div>
                `;
                messages.appendChild(botDiv);
                messages.scrollTop = messages.scrollHeight;
                btn.disabled = false;
            }, 800);
        }

        function handleChatKeyPress(e) {
            if (e.key === 'Enter') sendChatMessage();
        }

        function escapeHtml(text) {
            const div = document.createElement('div');
            div.textContent = text;
            return div.innerHTML;
        }

        // --- INIT ---
        window.addEventListener('scroll', () => {
            const navbar = document.getElementById('navbar');
            if (window.scrollY > 50) {
                navbar.classList.add('scrolled');
            } else {
                navbar.classList.remove('scrolled');
            }
        });

        window.onload = () => {
            createCosmicBackground();
        };